selectItemByIdxAndUserIdx


    select * from board

        onSale != 0

            and boardIdx < #{boardIdx}


    order by boardIdx desc
    limit 0, 10





    delete from board
    where boardIdx = #{param1}
    and userIdx = #{param2}





    update board set
    title = #{title}, content = #{content}, category = #{category},
    thumbnail = #{thumbnail}, imgURL = #{imgURL}, price = #{price}
    where boardIdx = #{boardIdx}
    and userIdx = #{userIdx}





    select * from board
    where userIdx = #{userIdx}
    order by boardIdx desc
